import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Image,
} from 'react-native';
import { useRoute } from '@react-navigation/native';

export default function AccountInfoScreen() {
  const route = useRoute();
  const { userData } = route.params || {};

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity>
          <Text style={styles.backButton}>{'<'}</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Thông tin tài khoản</Text>
      </View>

      {/* Tab Section */}
      <View style={styles.tabContainer}>
        <TouchableOpacity style={[styles.tabButton, styles.activeTab]}>
          <Text style={styles.tabTextActive}>Thanh toán</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.tabButton}>
          <Text style={styles.tabText}>Tiền gửi</Text>
        </TouchableOpacity>
      </View>

      {/* Content Section */}
      <Text style={styles.sectionTitle}>Quản lý nickname tài khoản</Text>
      <TouchableOpacity style={styles.accountBox}>
        <View>
          <Text style={styles.accountLabel}>Tài khoản thanh toán</Text>
          <Text style={styles.accountNumber}>
            {userData?.accountNumber || '6500205163687'}
          </Text>
        </View>
        <View>
          <Text style={styles.accountBalance}>
            {userData?.balance ? `${userData.balance} VND` : '1,490,814 VND'}
          </Text>
        </View>
      </TouchableOpacity>

      {/* Avatar Section */}
      <View style={styles.avatarContainer}>
        <Image
          source={require('../assets/avatar.jpg')} // Thay đổi với đường dẫn ảnh avatar
          style={styles.avatar}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#b91350',
    paddingVertical: 10,
    paddingHorizontal: 16,
  },
  backButton: {
    color: '#fff',
    fontSize: 18,
    marginRight: 10,
  },
  headerTitle: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  tabContainer: {
    flexDirection: 'row',
    backgroundColor: '#f4f4f4',
    justifyContent: 'center',
    paddingVertical: 10,
  },
  tabButton: {
    flex: 1,
    alignItems: 'center',
  },
  activeTab: {
    borderBottomWidth: 2,
    borderBottomColor: '#b91350',
  },
  tabText: {
    fontSize: 16,
    color: '#888',
  },
  tabTextActive: {
    fontSize: 16,
    color: '#b91350',
    fontWeight: 'bold',
  },
  sectionTitle: {
    marginTop: 10,
    marginLeft: 16,
    color: '#b91350',
    fontSize: 16,
    fontWeight: 'bold',
  },
  accountBox: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    marginHorizontal: 16,
    marginVertical: 10,
    padding: 16,
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#f0b90b',
  },
  accountLabel: {
    color: '#888',
    fontSize: 14,
  },
  accountNumber: {
    color: '#000',
    fontSize: 16,
    fontWeight: 'bold',
  },
  accountBalance: {
    color: '#000',
    fontSize: 16,
    fontWeight: 'bold',
  },
  avatarContainer: {
    alignItems: 'center',
    marginTop: 30,
  },
  avatar: {
    width: 60,
    height: 60,
    borderRadius: 30,
  },
});
