import React from "react";
import { View, Text, StyleSheet, TextInput, TouchableOpacity } from "react-native";

const AccountInfoScreen = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Thông tin tài khoản</Text>
      </View>
      <View style={styles.tabContainer}>
        <TouchableOpacity style={[styles.tab, styles.activeTab]}>
          <Text style={styles.tabTextActive}>Thanh toán</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.tab}>
          <Text style={styles.tabText}>Tiền gửi</Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity style={styles.manageNickname}>
        <Text style={styles.manageNicknameText}>Quản lý nickname tài khoản</Text>
      </TouchableOpacity>
      <View style={styles.accountCard}>
        <View style={styles.accountInfo}>
          <Text style={styles.label}>Tài khoản thanh toán</Text>
          <Text style={styles.accountNumber}>6500205163687</Text>
        </View>
        <View style={styles.balanceInfo}>
          <Text style={styles.label}>Số dư khả dụng</Text>
          <Text style={styles.balance}>1,490,814 VND</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    paddingHorizontal: 16,
    paddingTop: 40,
  },
  header: {
    alignItems: "center",
    marginBottom: 16,
  },
  headerText: {
    fontSize: 18,
    fontWeight: "bold",
  },
  tabContainer: {
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: 16,
  },
  tab: {
    flex: 1,
    paddingVertical: 12,
    alignItems: "center",
    backgroundColor: "#f7f7f7",
  },
  activeTab: {
    borderBottomColor: "#B22222",
    borderBottomWidth: 2,
  },
  tabText: {
    fontSize: 16,
    color: "#666",
  },
  tabTextActive: {
    fontSize: 16,
    color: "#B22222",
    fontWeight: "bold",
  },
  manageNickname: {
    marginVertical: 12,
    paddingVertical: 12,
    backgroundColor: "#f7f7f7",
    paddingHorizontal: 16,
  },
  manageNicknameText: {
    fontSize: 14,
    color: "#B22222",
  },
  accountCard: {
    backgroundColor: "#fff",
    borderRadius: 8,
    padding: 16,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 4,
    elevation: 3,
  },
  accountInfo: {
    marginBottom: 12,
  },
  label: {
    fontSize: 14,
    color: "#666",
  },
  accountNumber: {
    fontSize: 16,
    fontWeight: "bold",
  },
  balanceInfo: {},
  balance: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#B22222",
  },
});

export default AccountInfoScreen;
